g.script:
      
        // Get all of the elements.
        var ids = 'g1 g2 rect1'.split(' ');
        for (var i in ids)
        {
          this[ids[i]] = document.getElementById(ids[i]);
        }

        g2.addEventListener
        (
          'click',
          function(e)
          {
             e.stopPropagation();
             rect1.setAttributeNS(null, 'fill', 'blue');
          },
          false
        );

        g1.addEventListener
        (
          'click',
          function(e)
          {
             rect1.setAttributeNS(null, 'fill', 'red');
          },
          false
        );
